mapper.select:

SELECT announcement_serial,  announcement_title,  announcement_content,  announcement_date, announcement_hidden,  announcement_view,  announcement_status,announcement_priorityStatus  ,announcement_cnt
FROM	(SELECT /*+INDEX_DESC(ANNOUNCEMENT pk_ANNOUNCEMENT_serial) */
		     	rownum rn, announcement_serial,  announcement_title,  announcement_content,  announcement_date, announcement_hidden,  announcement_view,  announcement_status, announcement_priorityStatus, announcement_cnt 
	       from announcement 
          where announcement_status = 'Y'



            and  rownum <= #{pageNum} * #{amount}

		)
where rn > (#{pageNum}-1) * #{amount}
