mapper.select:

SELECT *
FROM (SELECT rownum rn, mem_no, ex_res_no, name, ex_date, payment_status, payment_amt
      FROM vw_ex_re 
	  WHERE ex_res_no > 0
	  AND mem_no = #{memNo}



	  AND rownum <= #{pageNum} * #{amount}  
		)
WHERE rn > (#{pageNum}-1) * #{amount}
ORDER BY ex_res_no DESC

    	select *
		 from exhibition
		where ex_no = #{exNo}

select * 
from exhibition
where ex_no = #{exNo}

select r.rev_no, r.score, r.content, r.reply_content, m.name, e.name, e.ex_no, m.mem_no
from review r , member m, exhibition e
where r.mem_no = m.mem_no
and m.mem_no = e.mem_no
and ex_no = #{exNo}
and m.mem_no = #{memNo}

select * 
from
    (select a.* , ROWNUM rn 
    from
        (select *
        from exhibition 
            where address like ('%'||#{exVO.address}||'%')
            and  approval_status = '00401'
            and end_date > sysdate

            and trunc(start_date) >= #{exVO.startDate}


            and trunc(end_date) <=#{exVO.endDate}

            and name like ('%'||#{keyword}||'%') ) a
        where 
        rownum < #{pageNum} * #{amount}
    )b
where rn > (#{pageNum}-1) * #{amount}

select count(*)
from exhibition
where address like ('%'||#{exVO.address}||'%')

		and start_date > #{exVO.startDate}


		and end_date < #{exVO.endDate}

and name like ('%'||#{exVO.name}||'%')

mapper.update:
update  exhibition_reservation  
set payment_status = 'N'
where ex_res_no = #{exResNo}
