mapper.select:
SELECT *
FROM
    (
    SELECT a.*,ROWNUM rn
    FROM
        (
        SELECT *
         FROM exhibition
        ) a
    WHERE ROWNUM < #{pageNum} * #{amount}
    ) b
WHERE rn > (#{pageNum}-1) * #{amount}

select * 
from
    (select a.* , ROWNUM rn 
    from
        (select *
        from exhibition 
            where address like ('%'||#{exVO.address}||'%')

            and start_date > #{exVO.startDate}
            and end_date <#{exVO.endDate}

            and name like ('%'||#{keyword}||'%') ) a
        where 
        rownum < #{pageNum} * #{amount}
    )b
where rn > (#{pageNum}-1) * #{amount}

select count(*)
from exhibition
where address like ('%'||#{exVO.address}||'%')

		and start_date > #{exVO.startDate}
		and end_date < #{exVO.endDate}

		and name like ('%'||#{exVO.name}||'%')
